import { StyleSheet, Text, View, FlatList } from "react-native";
import NewsCards from "./components/NewsCards";
import React, { useState, useEffect } from "react";
import { TextInput } from "react-native-paper";

export default function App() {
	const [data, setData] = useState([]);
	const [query, setQuery] = useState("");
	const [news, setNews] = useState([]);
	const [errorMessage, setErrorMessage] = useState();
	useEffect(() => {
		searchHandler();
	}, []);

	const searchHandler = async () => {
		try {
			const search = `http://newsapi.org/v2/top-headlines?country=us&q=${query}&apiKey=${Env.NEWS_API_KEY}`;
			const response = await fetch(search);
			const responseJson = await response.json();
			if (response.ok) {
				setSearchResults(responseJson.articles);
			} else setErrorMessage(responseJson.message);
		} catch (error) {
			console.log("Error", error);
			setErrorMessage(error.message);
		}
	};

	const updateQuery = (input) => {
		setNews(data.slice());
		setQuery(input);
	};
	const renderItem = ({ item }) => (
		<NewsCards
			title={item.title}
			description={item.description}
			urlToImage={item.urlToImage}
			content={item.content}
		/>
	);
	return (
		<View style={styles.container}>
			<Text style={styles.heading}>Search Hero</Text>
			<TextInput
				onChangeText={updateQuery}
				value={query}
				placeholder="Type Here..."
			/>
			<FlatList
				data={news}
				keyExtractor={(i) => i.id.toString()}
				extraData={query}
				renderItem={({ item }) => (
					<Text style={styles.flatList}>{filterNames(item)}</Text>
				)}
			/>
		</View>
	);
}

const styles = StyleSheet.create({
	container: {
		marginBottom: 45,
	},
	heading: {
		marginTop: 50,
		marginBottom: 10,
		marginLeft: 15,
		fontSize: 25,
	},
	flatList: {
		paddingLeft: 15,
		marginTop: 15,
		paddingBottom: 15,
		fontSize: 20,
		borderBottomColor: "#26a69a",
		borderBottomWidth: 1,
	},
});
