import React, { useState, useEffect } from "react";

import {
	StyleSheet,
	StatusBar,
	Text,
	View,
	FlatList,
	Button,
	ScrollView,
} from "react-native";
import NewsCards from "./NewsCards";
import Colors from "../constants/Colors";
import Env from "../constants/Env";
import { useNavigation } from "@react-navigation/native";
import { Appbar } from "react-native-paper";
function Categories() {
	const [newsDataEnt, setNewsDataEnt] = useState();
	const [newsDataGen, setNewsDataGen] = useState();
	const [newsDataHel, setNewsDataHel] = useState();
	const [newsDataSci, setNewsDataSci] = useState();

	const [newsDataTech, setNewsDataTech] = useState();

	const [errorMessage, setErrorMessage] = useState();
	const navigation = useNavigation();
	const load = async () => {
		try {
			const newsApientertaiment = `http://newsapi.org/v2/top-headlines?country=us&category=entertainment&pageSize=5&apiKey=${Env.NEWS_API_KEY}`;

			const response = await fetch(newsApientertaiment);
			const responseJson = await response.json();

			if (response.ok) {
				setNewsDataEnt(responseJson.articles);
			} else setErrorMessage(responseJson.message);
		} catch (error) {
			console.log("Error", error);
			setErrorMessage(error.message);
		}
	};

	const load2 = async () => {
		try {
			const newsApiGen = `http://newsapi.org/v2/top-headlines?country=us&category=general&pageSize=5&apiKey=${Env.NEWS_API_KEY}`;

			const response = await fetch(newsApiGen);
			const responseJson = await response.json();

			if (response.ok) {
				setNewsDataGen(responseJson.articles);
			} else setErrorMessage(responseJson.message);
		} catch (error) {
			console.log("Error", error);
			setErrorMessage(error.message);
		}
	};

	const load3 = async () => {
		try {
			const newsApiHel = `http://newsapi.org/v2/top-headlines?country=us&category=health&pageSize=5&apiKey=${Env.NEWS_API_KEY}`;

			const response = await fetch(newsApiHel);
			const responseJson = await response.json();

			if (response.ok) {
				setNewsDataHel(responseJson.articles);
			} else setErrorMessage(responseJson.message);
		} catch (error) {
			console.log("Error", error);
			setErrorMessage(error.message);
		}
	};

	const load4 = async () => {
		try {
			const newsApiSci = `http://newsapi.org/v2/top-headlines?country=us&category=science&pageSize=5&apiKey=${Env.NEWS_API_KEY}`;

			const response = await fetch(newsApiSci);
			const responseJson = await response.json();

			if (response.ok) {
				setNewsDataSci(responseJson.articles);
			} else setErrorMessage(responseJson.message);
		} catch (error) {
			console.log("Error", error);
			setErrorMessage(error.message);
		}
	};

	const load5 = async () => {
		try {
			const newsApiTech = `http://newsapi.org/v2/top-headlines?country=us&category=technology&pageSize=5&apiKey=${Env.NEWS_API_KEY}`;

			const response = await fetch(newsApiTech);
			const responseJson = await response.json();

			if (response.ok) {
				setNewsDataTech(responseJson.articles);
			} else setErrorMessage(responseJson.message);
		} catch (error) {
			console.log("Error", error);
			setErrorMessage(error.message);
		}
	};

	useEffect(() => {
		load();
		load2();
		load3();
		load4();
		load5();
	}, []);

	const renderItem = ({ item }) => (
		<NewsCards
			title={item.title}
			description={item.description}
			urlToImage={item.urlToImage}
			content={item.content}
		/>
	);

	return (
		<View style={styles.container}>
			<StatusBar backgroundColor={Colors.primary} barStyle="default" />
			<Appbar>
				<Button title="Go Back" onPress={() => navigation.goBack()} />

				<View>
					<Button
						title="us"
						onPress={() => {
							/* 1. Navigate to the Details route with params */
							navigation.navigate("main");
						}}
					/>

					<Button
						title="gb"
						onPress={() => {
							/* 1. Navigate to the Details route with params */
							navigation.navigate("mainGB");
						}}
					></Button>
				</View>
			</Appbar>
			<ScrollView>
				<Text style={styles.header}>Top News Us</Text>

				<Text style={styles.categoryTitle}>Top News for entertainment</Text>
				{newsDataEnt ? (
					<FlatList
						horizontal
						data={newsDataEnt}
						renderItem={renderItem}
						keyExtractor={(item) => item.publishedAt}
						style={styles.flatList}
					/>
				) : (
					errorMessage && (
						<Text style={styles.errMsg}>Error: {errorMessage}</Text>
					)
				)}
				<Text style={styles.categoryTitle}>Top News for general</Text>
				{newsDataGen ? (
					<FlatList
						horizontal
						data={newsDataGen}
						renderItem={renderItem}
						keyExtractor={(item) => item.publishedAt}
					/>
				) : (
					errorMessage && (
						<Text style={styles.errMsg}>Error: {errorMessage}</Text>
					)
				)}
				<Text style={styles.categoryTitle}>Top News for Health</Text>
				{newsDataHel ? (
					<FlatList
						horizontal
						data={newsDataHel}
						renderItem={renderItem}
						keyExtractor={(item) => item.publishedAt}
					/>
				) : (
					errorMessage && (
						<Text style={styles.errMsg}>Error: {errorMessage}</Text>
					)
				)}
				<Text style={styles.categoryTitle}>Top News for Science</Text>
				{newsDataSci ? (
					<FlatList
						horizontal
						data={newsDataSci}
						renderItem={renderItem}
						keyExtractor={(item) => item.publishedAt}
					/>
				) : (
					errorMessage && (
						<Text style={styles.errMsg}>Error: {errorMessage}</Text>
					)
				)}
				<Text style={styles.categoryTitle}>Top News for Technology</Text>
				{newsDataTech ? (
					<FlatList
						horizontal
						data={newsDataTech}
						renderItem={renderItem}
						keyExtractor={(item) => item.publishedAt}
					/>
				) : (
					errorMessage && (
						<Text style={styles.errMsg}>Error: {errorMessage}</Text>
					)
				)}
			</ScrollView>
		</View>
	);
}
const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: Colors.primary,
	},
	header: {
		color: "white",
		alignItems: "center",
		fontWeight: "bold",
		textAlign: "center",
		marginTop: 20,
		marginBottom: 20,
		fontSize: 30,
	},

	categoryTitle: {
		color: "white",
		fontWeight: "bold",
		fontSize: 20,
		marginLeft: 10,
		marginTop: 10,
		marginBottom: 10,
	},
	errMsg: {
		fontSize: 18,
		justifyContent: "center",
		alignItems: "center",
		color: "white",
	},
});

export default Categories;
