import React, { useState, useEffect } from "react";
import {
	StyleSheet,
	StatusBar,
	Text,
	View,
	FlatList,
	Button,
	TextInput,
} from "react-native";
import NewsCards from "./NewsCards";
import Colors from "../constants/Colors";
import Env from "../constants/Env";
import { useNavigation } from "@react-navigation/native";
import { Appbar } from "react-native-paper";

const search = () => {
	const [newsData, setNewsData] = useState();
	const [errorMessage, setErrorMessage] = useState();
	const navigation = useNavigation();
	const [searchValue, setSearchValue] = useState("");
	const [searchResults, setSearchResults] = useState(null);

	const searchHandler = async () => {
		try {
			const search = `http://newsapi.org/v2/top-headlines?country=us&q=${searchValue}&apiKey=${Env.NEWS_API_KEY}`;
			const response = await fetch(search);
			const responseJson = await response.json();
			if (response.ok) {
				setSearchResults(responseJson.articles);
			} else setErrorMessage(responseJson.message);
		} catch (error) {
			console.log("Error", error);
			setErrorMessage(error.message);
		}
	};

	useEffect(() => {
		searchHandler();
	}, []);

	const load = async () => {
		try {
			const newsApi = `http://newsapi.org/v2/top-headlines?country=us&apiKey=${Env.NEWS_API_KEY}`;
			const response = await fetch(newsApi);
			const responseJson = await response.json();

			if (response.ok) {
				setNewsData(responseJson.articles);
			} else setErrorMessage(responseJson.message);
		} catch (error) {
			console.log("Error", error);
			setErrorMessage(error.message);
		}
	};

	useEffect(() => {
		load();
	}, []);

	const renderItem = ({ item }) => (
		<NewsCards
			title={item.title}
			description={item.description}
			urlToImage={item.urlToImage}
			content={item.content}
		/>
	);

	return (
		<View style={styles.container}>
			<StatusBar backgroundColor={Colors.primary} barStyle="default" />
			<Appbar>
				<Button title="Go Back" onPress={() => navigation.goBack()} />

				<View>
					<Button
						title="us"
						onPress={() => {
							/* 1. Navigate to the Details route with params */
							navigation.navigate("main");
						}}
					/>

					<Button
						title="gb"
						onPress={() => {
							/* 1. Navigate to the Details route with params */
							navigation.navigate("mainGB");
						}}
					></Button>
				</View>
			</Appbar>
			<TextInput
				placeholder="Search"
				onChange={(e) => setSearchValue(e.target.value)}
				onKeyPress={searchHandler}
				placeholderTextColor={"#888888"}
			/>
			<View style={styles.header}>Top News US</View>
			{searchResults
				? searchResults.map((element, index) => (
						<FlatList
							data={searchValue}
							renderItem={renderItem}
							keyExtractor={(item) => item.publishedAt}
							key={index}
						/>
				  ))
				: errorMessage && (
						<Text style={styles.errMsg}>Error: {errorMessage}</Text>
				  )}
		</View>
	);
};

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: Colors.primary,
	},
	header: {
		height: 50,
		backgroundColor: Colors.primary,
		alignItems: "center",
	},
	title: {
		fontSize: 28,
		color: "white",
	},
	errMsg: {
		fontSize: 18,
		justifyContent: "center",
		alignItems: "center",
		color: "white",
	},
});

export default search;
