import React,{ useState, useEffect }from "react";
import { Text } from "react-native";
function Categories() {
	const [newsData, setNewsData] = useState();
	const [errorMessage, setErrorMessage] = useState();
const load = async () => {
	try {
		const newsApientertaiment = `http://newsapi.org/v2/top-headlines?country=us&category=entertainment&pageSize=10&apiKey=${Env.NEWS_API_KEY}`;
		const response = await fetch(newsApientertaiment);
		const responseJson = await response.json();

		if (response.ok) {
			setNewsData(responseJson.articles);
		} else setErrorMessage(responseJson.message);
	} catch (error) {
		console.log("Error", error);
		setErrorMessage(error.message);
	}
};

	
useEffect(() => {
	load();
}, []);




	const renderItem = ({ item }) => (
		<NewsCards
			title={item.title}
			description={item.description}
			urlToImage={item.urlToImage}
			content={item.content}
		/>
	);
	






	return (
		<View style={styles.container}>
			<StatusBar backgroundColor={Colors.primary} barStyle="default" />
			<Appbar>
				<Button title="Go Back" onPress={() => navigation.goBack()} />

				<View>
					<Button
						title="us"
						onPress={() => {
							/* 1. Navigate to the Details route with params */
							navigation.navigate("main");
						}}
					/>

					<Button
						title="gb"
						onPress={() => {
							/* 1. Navigate to the Details route with params */
							navigation.navigate("mainGB");
						}}
					></Button>
				</View>
			</Appbar>
			<View style={styles.header}>Top News US</View>
			{newsData ? (
				<FlatList
					data={newsData}
					renderItem={renderItem}
					keyExtractor={(item) => item.publishedAt}
				/>
			) : (
				errorMessage && <Text style={styles.errMsg}>Error: {errorMessage}</Text>
			)}
		</View>
	);
}

export default Categories;
