import React, { useState, useEffect } from "react";
import {
	StyleSheet,
	StatusBar,
	Text,
	View,
	FlatList,
	SafeAreaView,
	Button,
	TouchableOpacity,
} from "react-native";
import NewsCards from "../NewsCard/NewsCards";
import Colors from "../../constants/Colors";
import Env from "../../constants/Env";
import { useNavigation } from "@react-navigation/native";
import { Appbar, TextInput } from "react-native-paper";

const searchGb = () => {
	const [errorMessage, setErrorMessage] = useState();
	const navigation = useNavigation();
	const [searchValue, setSearchValue] = useState("");
	const [searchResults, setSearchResults] = useState([]);

	const searchHandler = async () => {
		try {
			const search = `http://newsapi.org/v2/top-headlines?country=gb&q=${searchValue}&apiKey=${Env.NEWS_API_KEY}`;
			const response = await fetch(search);
			const responseJson = await response.json();
			if (response.ok) {
				setSearchResults(responseJson.articles);
			} else setErrorMessage(responseJson.message);
		} catch (error) {
			console.log("Error", error);
			setErrorMessage(error.message);
		}
	};

	useEffect(() => {
		searchHandler();
	}, []);

	const renderItem = ({ item }) => (
		<NewsCards
			title={item.title}
			description={item.description}
			urlToImage={item.urlToImage}
			content={item.content}
		/>
	);

	return (
		<SafeAreaView style={styles.container}>
			<StatusBar backgroundColor={Colors.primary} barStyle="default" />
			<Appbar style={styles.appbar}>
				<Appbar.BackAction onPress={() => navigation.goBack()} />

				<View style={styles.right}>
					<TouchableOpacity
						style={styles.lang}
						onPress={() => {
							navigation.navigate("SearchUs");
						}}
					>
						<Text style={{ color: "#ffffff" }}>US</Text>
					</TouchableOpacity>
					<TouchableOpacity
						style={styles.lang}
						onPress={() => {
							navigation.navigate("SearchGb");
						}}
					>
						<Text style={{ color: "#ffffff" }}>GB</Text>
					</TouchableOpacity>
				</View>
			</Appbar>
			<TextInput
				placeholder="Search"
				onChange={(e) => setSearchValue(e.target.searchValue)}
				onChangeText={searchHandler}
				placeholderTextColor={"#888888"}
				style={styles.search}
			/>

			<Text style={styles.title}>Search Top News In Gb by Term</Text>

			{searchResults ? (
				<FlatList
					data={searchResults}
					renderItem={renderItem}
					keyExtractor={(item) => item.publishedAt}
				/>
			) : (
				errorMessage && <Text style={styles.errMsg}>Error: {errorMessage}</Text>
			)}
		</SafeAreaView>
	);
};

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: Colors.primary,
	},
	title: {
		fontSize: 24,
		fontWeight: "bold",
		color: "white",
		textAlign: "center",
		marginTop: 20,
		marginBottom: 20,
	},

	errMsg: {
		fontSize: 18,
		justifyContent: "center",
		alignItems: "center",
		color: "white",
	},
	header: {
		color: "white",
		alignItems: "center",
		fontWeight: "bold",
		textAlign: "center",
		marginTop: 20,
		marginBottom: 20,
		fontSize: 30,
	},
	search: {
		width: "100%",
		backgroundColor: "white",
		height: 80,
		padding: 10,
		fontSize: 30,
	},
	lang: {
		backgroundColor: "#33BDFF",
		textAlign: "center",
		justifyContent: "center",
		marginRight: 10,
		alignContent: "center",
		alignItems: "center",

		fontSize: 18,
		width: 30,
		height: 30,
	},
	appbar: {
		display: "flex",
		flexDirection: "row",
	},

	appbar: {
		position: "relative",
		flexDirection: "row",
	},
	right: {
		position: "absolute",
		right: 10,
		flexDirection: "row",
	},
	rightButtons: {
		alignSelf: "flex-end",
		flex: 1,
	},
	topBtn: {},
});

export default searchGb;
