import React, { useState, useEffect } from "react";
import NewsCards from "./NewsCards";
import Colors from "../constants/Colors";
import Env from "../constants/Env";
("react-native");
import {
	StyleSheet,
	StatusBar,
	Text,
	View,
	FlatList,
	Button,
	ScrollView,
} from "react-native";

import { useNavigation } from "@react-navigation/native";
import { Appbar } from "react-native-paper";
function SingleCategory({ route }) {
	const { category } = route.params;
	const [categoryNew, setCategory] = useState();

	const [errorMessage, setErrorMessage] = useState();
	const navigation = useNavigation();
	const load = async () => {
		try {
			const categoryGet = `http://newsapi.org/v2/top-headlines?country=us&category=${category}&pageSize=10&apiKey=${Env.NEWS_API_KEY}`;

			const response = await fetch(categoryGet);
			const responseJson = await response.json();

			if (response.ok) {
				setCategory(responseJson.articles);
			} else setErrorMessage(responseJson.message);
		} catch (error) {
			console.log("Error", error);
			setErrorMessage(error.message);
		}
	};

	useEffect(() => {
		load();
	}, [category]);

	const renderItem = ({ item }) => (
		<NewsCards
			title={item.title}
			description={item.description}
			urlToImage={item.urlToImage}
			content={item.content}
		/>
	);

	return (
		<View style={styles.container}>
			<StatusBar backgroundColor={Colors.primary} barStyle="default" />
			<Appbar style={styles.appbar}>
				<Appbar.BackAction onPress={() => navigation.goBack("main")} />

				<View style={styles.right}>
					<Button
						style={styles.lng}
						title="us"
						onPress={() => {
							navigation.navigate("Categories");
						}}
					/>

					<Button
						style={styles.lng}
						title="gb"
						onPress={() => {
							navigation.navigate("CategoriesGb");
						}}
					></Button>
				</View>
			</Appbar>
			<ScrollView>
				<Text style={styles.header}>Top News for {category}</Text>

				{newsDataEnt ? (
					<FlatList
						horizontal
						data={category}
						renderItem={renderItem}
						keyExtractor={(item) => item.publishedAt}
						style={styles.flatList}
					/>
				) : (
					errorMessage && (
						<Text style={styles.errMsg}>Error: {errorMessage}</Text>
					)
				)}
			</ScrollView>
		</View>
	);
}
const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: Colors.primary,
	},

	button: {
		width: 120,
		height: 50,
		marginBottom: 20,
		marginTop: 100,
		flex: 2,
		backgroundColor: "#3366ff",
		justifyContent: "center",
		alignItems: "center",
		marginRight: 10,
	},
	more: {
		color: "white",
		fontSize: 22,
		textTransform: "uppercase",
	},
	appbar: {
		position: "relative",
		flexDirection: "row",
	},
	right: {
		position: "absolute",
		right: 10,
		flexDirection: "row",
	},
	header: {
		color: "white",
		alignItems: "center",
		fontWeight: "bold",
		textAlign: "center",
		marginTop: 20,
		marginBottom: 20,
		fontSize: 30,
	},
	lng: {
		height: "",
		backgroundColor: "red",
	},
	categoryTitle: {
		color: "white",
		fontWeight: "bold",
		fontSize: 20,
		marginLeft: 10,
		marginTop: 10,
		marginBottom: 10,
	},
	errMsg: {
		fontSize: 18,
		justifyContent: "center",
		alignItems: "center",
		color: "white",
	},
});

export default SingleCategory;
